const cardButtons=document.querySelectorAll(".cards__button"),modal=document.querySelector(".modal"),modalContent=document.querySelector(".modal__content");cardButtons.forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault(),modal.classList.add("modal-show")}))})),modalContent.addEventListener("click",(function(e){e.stopPropagation()})),modal.addEventListener("click",(function(e){e.preventDefault(),modal.classList.remove("modal-show")})),window.addEventListener("keydown",(function(e){"Esc"!==e.key&&"Escape"!==e.key||(e.preventDefault(),modal.classList.contains("modal-show")&&modal.classList.remove("modal-show"))}));