const promoButton=document.querySelector(".promo__button"),modal=document.querySelector(".modal");promoButton.addEventListener("click",(function(o){o.preventDefault(),modal.classList.add("modal-show")})),window.addEventListener("keydown",(function(o){"Esc"!==o.key&&"Escape"!==o.key||(o.preventDefault(),modal.classList.contains("modal-show")&&modal.classList.remove("modal-show"))}));